    private void startGameMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_startGameMouseClicked
        
        setUnits();
        if(atLeastOne(units)){
            vc.switchStates();
        }
        
    }//GEN-LAST:event_startGameMouseClicked
    public boolean[] getUnits(){return units;}
