    //Need to reuse game frame
    private void setUnits(){
        units[0] = unit13Check.isSelected();
        units[1] = unit14Check.isSelected();
        units[2] = unit15Check.isSelected();
        units[3] = unit16Check.isSelected();
        units[4] = unit17Check.isSelected();
        units[5] = unit18Check.isSelected();
        units[6] = unit19Check.isSelected();
        units[7] = unit20Check.isSelected();
        units[8] = unit21Check.isSelected();
        units[9] = unit22Check.isSelected();
        units[10] = unit23Check.isSelected();
        units[11] = unit24Check.isSelected();
    }
     /**
     * Computes if at least one check box is selected
     * @param u
     * @return 
     */
    private boolean atLeastOne(boolean[] u){
        for(int i=0; i < u.length; ++i){
            if(u[i] == true) return true;
        }
        JOptionPane.showMessageDialog(this,"At least one unit must be selected!","Error!",JOptionPane.ERROR_MESSAGE);
        return false;
    }
